(function(){var topics={};var API={init:function(){},configure:function(){},subscribe:function(topic,listener){if(!topics[topic])topics[topic]=[];topics[topic].push(listener)},unsubscribe:function(topic,callback){topics[topic]=topics[topic].filter(function(listener){return listener!==callback})},emit:function(topic,data){if(!topics[topic]||topics[topic].length<1)return;topics[topic].forEach(function(listener){listener(data||{})})}};window.ApplicationBus=API})()